import { useDrag } from "react-dnd";

const PictureList = ({id, title}) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: "image",
        item: {id: id},
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
            console
        }),
    }))

    return <div 
        className='item'
        ref={drag}>
        {title}
    </div>
}

export default PictureList;
