import React from 'react';
import { useDrag } from 'react-dnd';

const DragDrop = ({ boards }) => {

    const [{isDragging}, drag] = useDrag

    return (
        <>
        <div>
            <div
                className='board'>
                <div className='board__title'>
                    {boards.map(board => (
                        <div className='item' draggable={true}>{board.title}</div>
                    ))}
                </div>
            </div>
            <div
                className='board1'></div>
        </div>
        </>
    )
}

export default DragDrop;
