import { useDrag } from "react-dnd";

const PictureList = ({ id, title }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: "image",
        item: { id: id },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }))

    return <div>
        <div
            className='item'
            ref={drag}>
            {title}
        </div>
        <button>download</button>
    </div>
}

export default PictureList;
