import React, { useEffect, useState } from 'react';
import { useDrop } from 'react-dnd';
import PostService from '../api/postService';
import PictureList from './pictureList';


const DragDrop = () => {

    const [boards, setBoards] = useState([])
    const [area, setArea] = useState([])

    useEffect(async () => {
        const response = await PostService.getAll()
        const data = response.data
        setBoards(data)
      }, [boards])

    
      const [{ isOver }, drop] = useDrop(() => ({
        accept: "image",
        drop: (item) => addImageToBoard(item.id),
        collect: (monitor) => ({
          isOver: !!monitor.isOver(),
        }),
      }));

    const addImageToBoard = async (id) => {
        debugger
        const response = await PostService.getAll()
        const data = response.data
        setBoards(data)
        console.log(boards)
        const pictureList = [...boards.filter((board) => id === board.id)];
        debugger
        setArea((area) => [...area, pictureList[0]])
        console.log(area)
    }
  
    
    console.log(area)
    console.log(boards)

    return (
        <div>
            <div
                className='board1'>
                <div className='board__title'>
                    {boards.map(board => {
                        return <PictureList title={board.title} id={board.id}/>
                        })}
                </div>
            </div>
            <div
                className='board1' ref={drop}>
                <div className='board__title'>
                {area.map(are => {
                    
                    return <PictureList title={are.title} id={are.id}/>
                    })}
                </div>
            </div>
        </div>
    )
}

export default DragDrop;
