    //     download(filename, text);
    // }, false);

    return <div>
        <textarea id="text-val" rows="4">This is the content of my file</textarea><br/>
        <input  type="button" id="dwn-btn" value="Download dinamically generated text file"/>
    </div>
}

export default Button;
