import { useEffect, useState } from 'react';
import PostService from './api/postService';
import './App.css';
import Board from './boards/board';
// import Posts from './posts/posts';


const App = () => {

  // const [boards, setBoards] = useState([])
  const [boards, setBoards] = useState([
    {id: 1, title: 'done', items: [{id: 1, title: 'go tot the store'},{id: 2, title: 'throw something away'}]},
    {id: 2, title: 'done', items: [{id: 3, title: 'store'},{id: 4, title: 'throw something away'}]},
    {id: 3, title: 'done', items: [{id: 5, title: 'go '},{id: 6, title: 'throw something away'}]}
  ])
  const [currentBoard, setCurrentBoard] = useState(null)
  const [currentItem, setCurrentItem] = useState(null)

  // useEffect(async () => {
  //   const response = await PostService.getAll()
  //   const data = response.data
  //   setBoards(data)
  // }, [])



function dragOverHandler(e) {
    e.preventDefault()
    if(e.target.className == 'item') {
        e.target.style.boxShadow = '0 4px 3px gray'
    }
}

function dragLeaveHandler(e) {
    e.target.style.boxShadow = 'none'
}

function dragStartHandler (e, board, item) {
    setCurrentBoard(board)
    setCurrentItem(item)
}

function dragEndHandler(e) {
    e.target.style.boxShadow = 'none'
}

function dropHandler (e, board, item) {
    e.preventDefault()   
    const currentIndex = currentBoard.items.indexOf(currentItem)
    // currentBoard.items.splice(currentIndex, 1)
    const dropIndex = board.items.indexOf(item)
    // board.items.splice(dropIndex + 1, 0, currentItem)
    setBoards(boards.map(b => {
      if (b.id === board.id){
        return board
      }
      if(b.id === currentBoard.id) {
        return currentBoard
      }
      return b
    }))
}

  console.log(boards)
  return (
    <div className='app'>
      {boards.map(board => 
        <div className='board'>
          <div className='board__title'>
            {board.title}
          </div>
          {board.items.map(item => 
            <div 
              className='item'
              draggable={true}
              onDragOver={(e => dragOverHandler(e))}
              onDragLeave={e => dragLeaveHandler(e)}
              onDragStart={e => dragStartHandler(e, board, item)}
              onDragEnd={e => dragEndHandler(e)}
              onDrop={(e => dropHandler(e, board, item))}>{item.title}</div>
            )}
        </div>)}

        {/* {boards.map(board =>
          <p 
            className='item'
            draggable={true}
            onDragOver={(e => dragOverHandler(e))}
            onDragLeave={e => dragLeaveHandler(e)}
            onDragStart={e => dragStartHandler(e, board, )}
            onDragEnd={e => dragEndHandler(e)}
            onDrop={(e => dropHandler(e, board, ))}>{board.title}</p>
        )} */}
    </div>



  );
}

export default App;

