const Button = ({area}) => {

    const letMap = area.map((ar) => {
        return ar.title
    })

    
    

    const download = (letMap, fileName) => {
        const blob = new Blob([letMap])
        const link = document.createElement('a')
      
        link.href = window.URL.createObjectURL(blob)
        link.download = letMap
        link.click()
      }
    
    // Start file download.
    // document.getElementById("dwn-btn").addEventListener("click", function(){
    //     // Generate download of hello.txt file with some content
    //     var text = document.getElementById("text-val").value;
    //     var filename = "hello.txt";
        
    //     download(filename, text);
    // }, false);

    return <div>
        <p id="text-val" rows="4">{letMap}</p><br/>
        <input onClick={download} type="button" id="dwn-btn" value="Download dinamically generated text file"/>
    </div>
}

export default Button;
