const Button = ({area}) => {

    const letMap = area.map((ar) => {
        return ar.title
    })

    function download(letMap) {

        console.log(filename)
        var element = document.createElement('a');
        element.style.display = 'none';
        element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(filename));
        element.setAttribute('download', filename);
        element.style.display = 'none';
        document.body.appendChild(element);
        element.click();
        console.log(filename)
        document.body.removeChild(element);
      }


    // document.getElementById('download-btn').addEventListener('click', function(){
    //     var filename = document.getElementById('filename').value
    //     download(filename)
    // }, false)

    return <div>
        {/* <input type='text' id='filename' value={letMap}/> */}
        <p id='filename'>{letMap}</p>
        <button id='download-btn' onClick={download}>download</button>
    </div>
}

export default Button;
